            this.percent_profitable = this.start_balance == 0 ? 0 : (( this.net_profit / this.start_balance ) * 100 ).toFixed()
                            .toString()
                            .replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");
            this.start_balance = this.start_balance.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");
            this.current_balance = this.current_balance.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");
            this.net_profit = this.net_profit.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");
            this.equity = Number(last_timeline_item.equity.toFixed(2)).toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");
            this.used_margin = Number(last_timeline_item.usedMargin.toFixed(2)).toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");
            this.active_since=new Date(this.timeline_status[0].timeLine).toDateString().replace(/^\S+\s/,'');
            this.tradingUnits = response.data.trades;
            this.get_categories();
            this.setting_chart2_data();
