        });
    },
    updateMonthlynetProfits() {
      let dates = this.tradingUnits.map((u) => new Date(u.openTime));
      let netProfits = this.tradingUnits.map(
        (u) => Number(u.grossProfit) + 2 * Number(u.commission) + Number(u.swap)
      );
      let minDate = new Date(Math.min(...dates));
      let maxDate = new Date(Math.max(...dates));
      minDate.setMonth(minDate.getMonth(), 1);
      this.monthlynetProfits = [];
      this.monthlynetProfits[0] = ["Monat", "netProfit"];
      let iMonth = 0;
      for (
        let date = minDate;
        date <= maxDate;
        date.setMonth(date.getMonth() + 1)
      ) {
        let nextMonth = new Date(date.getFullYear(), date.getMonth() + 1, 1);
        let sum = 0;
        for (let i = 0; i < dates.length; i++) {
          if (dates[i] >= date && dates[i] < nextMonth) {
            sum += netProfits[i];
          }
        }
        iMonth++;
        var dateString = "" + (date.getMonth() + 1) + "." + date.getFullYear();
        this.monthlynetProfits[iMonth] = [dateString, sum];
      }
      this.monthlynetProfits[iMonth + 1] = ["8.2021", 20];
    },
    setting_chart1_data() {     
      let dates = (new Date()).getDate();
      let before_year1_date = new Date();
      before_year1_date.setDate(Number(dates) - 365);
      before_year1_date = before_year1_date.toISOString().split("T")[0];
      let before_mon6_date = new Date();
      before_mon6_date.setDate(Number(dates) - 180);
      before_mon6_date = before_mon6_date.toISOString().split("T")[0];
      let before_mon3_date = new Date();
      before_mon3_date.setDate(Number(dates) - 90);
      before_mon3_date = before_mon3_date.toISOString().split("T")[0];

      for (const date in this.chart1_data) {
        if (this.chart1_data[date] != null) {
          let roi_item = this.chart1_data[date];
          this.chart1_data[date].roi = Number(((roi_item.netProfit/roi_item.usedMargin)*100).toFixed(1));
          this.temp_chart1_all.push(this.chart1_data[date].roi).toFixed(1);
          this.cate_chart1_all.push(date);
          if ( date >= before_year1_date ) {
            this.temp_chart1_year1.push(this.chart1_data[date].roi).toFixed(1);
            this.cate_chart1_year1.push(date);
          }
          if ( date >= before_mon6_date ) {
            this.temp_chart1_mon6.push(this.chart1_data[date].roi).toFixed(1);
            this.cate_chart1_mon6.push(date);
          }
          if ( date >= before_mon3_date ) {
            this.temp_chart1_mon3.push(this.chart1_data[date].roi).toFixed(1);
            this.cate_chart1_mon3.push(date);
          }
        }
      }
      this.cate_week.map(weekday=>{
        this.chart1_data[weekday] == null ? this.temp_chart1_week.push(0) : this.temp_chart1_week.push(this.chart1_data[weekday]);
      });
      this.cate_mon1.map(monthday=>{
        this.chart1_data[monthday] == null ? this.temp_chart1_mon1.push(0) : this.temp_chart1_mon1.push(this.chart1_data[monthday]);
      });
    },
    setting_chart2_data() {
      
      let dates= (new Date()).getDate();
      let before_year1_date = new Date();
      before_year1_date.setDate(Number(dates) - 365);
      before_year1_date = before_year1_date.toISOString().split("T")[0];
      let before_mon6_date = new Date();
      before_mon6_date.setDate(Number(dates) - 180);
      before_mon6_date = before_mon6_date.toISOString().split("T")[0];
      let before_mon3_date = new Date();
      before_mon3_date.setDate(Number(dates) - 90);
      before_mon3_date = before_mon3_date.toISOString().split("T")[0];
      //Getting Max Drawdown
      this.timeline_status.map((item) => {
        if (item.drawDown < this.max_drawdown){
          this.max_drawdown = item.drawDown;
        }
        let opening_date = item.timeLine.split("T")[0];
        if (this.chart2_data[opening_date] == null) {
          this.chart2_data[opening_date] = {};
          this.chart2_data[opening_date].count = 0;
          this.chart2_data[opening_date].sum_equity = 0;
          this.chart2_data[opening_date].sum_balance = 0;
        }
        this.chart2_data[opening_date].count++;
        this.chart2_data[opening_date].sum_equity += item.equity;
        this.chart2_data[opening_date].sum_balance += item.balance;
      });
      this.max_drawdown = Number(-this.max_drawdown.toFixed()).toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");  
      for (const date in this.chart2_data) {
        if (this.chart2_data[date] != null) {
          let equity_item = this.chart2_data[date];
          this.chart2_data[date].equity = Number((equity_item.sum_equity/equity_item.count).toFixed(1));
          this.chart2_data[date].balance = Number((equity_item.sum_balance/equity_item.count).toFixed(1));
          this.temp_chart2_equity_all.push(equity_item.equity);
          this.temp_chart2_balance_all.push(equity_item.balance);
          this.cate_chart2_all.push(date);
          if ( date >= before_year1_date ) {
            this.temp_chart2_equity_year1.push(equity_item.equity);
            this.temp_chart2_balance_year1.push(equity_item.balance);
            this.cate_chart2_year1.push(date);
          }
          if ( date >= before_mon6_date ) {
            this.temp_chart2_equity_mon6.push(equity_item.equity);
            this.temp_chart2_balance_mon6.push(equity_item.balance);
            this.cate_chart2_mon6.push(date);
          }
          if ( date >= before_mon3_date ) {
            this.temp_chart2_equity_mon3.push(equity_item.equity);
            this.temp_chart2_balance_mon3.push(equity_item.balance);
            this.cate_chart2_mon3.push(date);
          }
        }
      }
         
      this.cate_week.map(weekday=>{
        this.chart2_data[weekday] == null ? this.temp_chart2_equity_week.push(0) : this.temp_chart2_equity_week.push(this.chart2_data[weekday].equity);
        this.chart2_data[weekday] == null ? this.temp_chart2_balance_week.push(0) : this.temp_chart2_balance_week.push(this.chart2_data[weekday].balance);
      });
      this.cate_mon1.map(monthday=>{
        this.chart2_data[monthday] == null ? this.temp_chart2_equity_mon1.push(0) : this.temp_chart2_equity_mon1.push(this.chart2_data[monthday].equity);
        this.chart2_data[monthday] == null ? this.temp_chart2_balance_mon1.push(0) : this.temp_chart2_balance_mon1.push(this.chart2_data[monthday].balance);
      });
    },
    setting_chart3_data() {
      for (const year in this.chart3_data) {
        if (this.chart3_data[year] != null) {
          for (const mon in this.chart3_data[year]) {
            if (this.chart3_data[year][mon] != null) {
              let roi_item = this.chart3_data[year][mon];
              this.chart3_data[year][mon].roi = Number(((roi_item.netProfit/roi_item.usedMargin)*100).toFixed());
            }
          }
        }
      }
    },
