    sel_chart4_year() {
      if (this.chart4_opt == "day") {
        let cur_year = new Date().getFullYear();
        let cur_month = new Date().getMonth() + 1;
        if (this.chart4_year == cur_year) {
          this.chart4_month = cur_month;
        }
        this.sel_chart4_month();
      } else {
        this.temp_chart4_categories = this.cate_months;
        this.temp_chart4_win_series = [];
        this.temp_chart4_lose_series = [];
        this.opt4_months.map((m) => {
          if (
            this.chart4_data[this.chart4_year] != null &&
            this.chart4_data[this.chart4_year][m.index] != null
          ) {
            this.temp_chart4_win_series.push(
              this.chart4_data[this.chart4_year][m.index].win.netProfit.toFixed(
                2
              )
            );
            this.temp_chart4_lose_series.push(
              (
                0 - this.chart4_data[this.chart4_year][m.index].lose.netProfit
              ).toFixed(2)
            );
          } else {
            this.temp_chart4_win_series.push(0);
            this.temp_chart4_lose_series.push(0);
          }
        });
        this.updateChart4();
      }
    },
    sel_chart4_month() {
      let day_count = this.opt4_days[this.chart4_month - 1];
      ///// leap year days setting
      if (Number(this.chart4_year) % 4 == 0 && this.chart4_month == 2)
        day_count = 29;
      this.temp_chart4_categories = [];
      this.temp_chart4_win_series = [];
      this.temp_chart4_lose_series = [];
      for (let i = 1; i <= day_count; i++) {
        this.temp_chart4_categories.push(i);
        if (this.chart4_data[i] != null) delete this.chart4_data[i];
        this.chart4_data[i] = {};
        this.chart4_data[i].trades = [];
        this.chart4_data[i].win = {};
        this.chart4_data[i].win.trades = [];
        this.chart4_data[i].win.volume = 0;
        this.chart4_data[i].win.netProfit = 0;
        this.chart4_data[i].lose = {};
        this.chart4_data[i].lose.trades = [];
        this.chart4_data[i].lose.volume = 0;
        this.chart4_data[i].lose.netProfit = 0;
      }
      if (
        this.chart4_data[this.chart4_year] != null &&
        this.chart4_data[this.chart4_year][this.chart4_month] != null
      ) {
        this.chart4_data[this.chart4_year][this.chart4_month].trades.map(
          (item) => {
            let item_day = Number(item.openTime.split("-")[2].substr(0, 2));
            this.chart4_data[item_day].trades.push(item);
            if (item.netProfit > 0) {
              this.chart4_data[item_day].win.trades.push(item);
              this.chart4_data[item_day].win.volume += item.volume;
              this.chart4_data[item_day].win.netProfit += item.netProfit;
            } else {
              this.chart4_data[item_day].lose.trades.push(item);
              this.chart4_data[item_day].lose.volume += item.volume;
              this.chart4_data[item_day].lose.netProfit += item.netProfit;
            }
          }
        );
        for (let i = 1; i <= day_count; i++) {
          this.temp_chart4_win_series.push(
            this.chart4_data[i].win.netProfit.toFixed(2)
          );
          this.temp_chart4_lose_series.push(
            Number(0 - this.chart4_data[i].lose.netProfit).toFixed(2)
          );
        }
      }
      this.updateChart4();
    },
