    counting_trades() {
      this.total_trade_count = this.tradingUnits.length;
      this.win_trade_count = 0;
      this.win_long_trade_count = 0;
      this.win_short_trade_count = 0;
      this.lose_trade_count = 0;
      this.lose_long_trade_count = 0;
      this.lose_short_trade_count = 0;
      this.tradingUnits_position = [];
      this.tradingUnits_history = [];
      //// Counting  profit, winning, losing, history, positions
      this.cate_years = [];
      this.tradingUnits.map(item => {  
        // Getting Symbols
        let duplicate_symbol = true;
        this.symbolNames.map((sym) => {
          if (item.symbolName == sym) {
            duplicate_symbol = false;
          }
        });
        if (duplicate_symbol) {
          let color =
            "#" + ((Math.random() * 0xffffff) << 0).toString(16);
          this.symbolNames.push(item.symbolName);
          this.opt_symbol_colors.push(color);
          this.series5.push({
            name: item.symbolName,
            data: [],
            color: color,
          });
        }      
        /// Getting year
        let opening_date = item.openTime.split("T")[0];
        let year = opening_date.split("-")[0];
        let mon = Number(opening_date.split("-")[1]);
        let yFlag = 1;
        this.cate_years.map((y) => {
          if (y == year) yFlag = 0;
        });
        if (yFlag) {
          this.cate_years.push(year);
          this.chart3_data[year] = {};
          this.chart4_data[year] = {};
          this.chart4_data[year].trades = [];
          this.chart4_data[year].win = {};
          this.chart4_data[year].win.volume = 0;
          this.chart4_data[year].win.netProfit = 0;
          this.chart4_data[year].lose = {};
          this.chart4_data[year].lose.volume = 0;
          this.chart4_data[year].lose.netProfit = 0;
        }
        
        if (this.chart1_data[opening_date] == null) {
          this.chart1_data[opening_date] = {};
          this.chart1_data[opening_date].netProfit = 0;
          this.chart1_data[opening_date].usedMargin = 0;
        }
        this.chart1_data[opening_date].netProfit += item.netProfit;
        this.chart1_data[opening_date].usedMargin += item.usedMargin;

        if (this.chart3_data[year][mon] == null) {
          this.chart3_data[year][mon] = {};
          this.chart3_data[year][mon].netProfit = 0;
          this.chart3_data[year][mon].usedMargin = 0;
        }
        this.chart3_data[year][mon].netProfit += item.netProfit;
        this.chart3_data[year][mon].usedMargin += item.usedMargin;
        this.chart4_data[year].trades.push(item);
        if (this.chart4_data[year][mon] == null) {
          this.chart4_data[year][mon] = {};
          this.chart4_data[year][mon].trades = [];
          this.chart4_data[year][mon].win = {};
          this.chart4_data[year][mon].win.volume = 0;
          this.chart4_data[year][mon].win.netProfit = 0;
          this.chart4_data[year][mon].lose = {};
          this.chart4_data[year][mon].lose.volume = 0;
          this.chart4_data[year][mon].lose.netProfit = 0;
        }
        this.chart4_data[year][mon].trades.push(item);

        if (item.netProfit > 0) {
          this.win_trade_count++;
          this.chart4_data[year].win.volume += item.volume;
          this.chart4_data[year].win.netProfit += item.netProfit;
          this.chart4_data[year][mon].win.volume += item.volume;
          this.chart4_data[year][mon].win.netProfit += item.netProfit;
          if (item.isBuy) this.win_long_trade_count++;
          else this.win_short_trade_count++;
        } else {
          this.lose_trade_count++;
          this.chart4_data[year].lose.volume += item.volume;
          this.chart4_data[year].lose.netProfit += item.netProfit;
          this.chart4_data[year][mon].lose.volume += item.VSolume;
          this.chart4_data[year][mon].lose.netProfit += item.netProfit;
          if (item.isBuy) this.lose_long_trade_count++;
          else this.lose_short_trade_count++;
        }
        if (item.status == 4) this.tradingUnits_history.push(item);
        else this.tradingUnits_position.push(item);
      });
      if (this.cate_years.length) {
        this.cate_years = this.cate_years.sort();
        this.chart4_year = this.cate_years[this.cate_years.length - 1];
      }
      //// Setting table data as following selected trading option
      if (this.sel_unit == "history")
        this.tradingUnits_temp = this.tradingUnits_history;
      else this.tradingUnits_temp = this.tradingUnits_position;

      this.chart3_year = this.chart4_year = this.cate_years[this.cate_years.length - 1];
      this.setting_chart1_data();
      this.setting_chart3_data();
    },
    calculating_during(seconds) {
      seconds = Number(seconds);
      let d = Math.floor(seconds / (3600 * 24));
      let h = Math.floor((seconds % (3600 * 24)) / 3600);
      let m = Math.floor((seconds % 3600) / 60);
      let s = Math.floor(seconds % 60);

      let dDisplay = d > 0 ? d + "d " : "";
      let hDisplay = h > 0 ? h + "h " : "";
      let mDisplay = m > 0 ? m + "m " : "";
      let sDisplay = s > 0 ? s + "s " : "";
      return dDisplay + hDisplay + mDisplay + sDisplay;
    },
    change_period(val) {
      //Setting items_period & items_symbol when period is changed
      this.sel_period = val;
      let cur_mon = new Date().getMonth() + 1;
      let cur_year = new Date().getYear();
      let tmp_tradingUnits = [];
      ////// Getting trading unit temp data of selected period
      if (val == "cur_month") {
        $("#sel_date_range").hide();
        tmp_tradingUnits = this.tradingUnits.filter(
          (u) =>
            Number(u.openTime.split("-")[1]) == cur_mon &&
            Number(u.openTime.split("-")[0]) == cur_year
        );
      } else {
        $("#sel_date_range").show();
        tmp_tradingUnits = this.tradingUnits.filter(
          (u) =>
            u.openTime.substr(0, 10) >= this.dates[0] &&
            u.openTime.substr(0, 10) <= this.dates[1]
        );
      }
      ////// Setting items_period of selected period
      ////// Initializing items_period
      this.items_period = [
        {
          summery: this.$t("netProfit"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
        {
          summery: this.$t("commission"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
        {
          summery: this.$t("total_trades"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
        {
          summery: this.$t("win_trades"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
        {
          summery: this.$t("largest_win_trades"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
        {
          summery: this.$t("lose_trades"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
        {
          summery: this.$t("largest_lose_trades"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
        {
          summery: this.$t("avg_trades"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
        {
          summery: this.$t("avg_trade_dur"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
        {
          summery: this.$t("vol_trades"),
          all_trades: 0,
          long_trades: 0,
          short_trades: 0,
        },
      ];

      ////// Initializing items_symbol

      this.initializing_symbol();

      ////// Start Setting items_period
      this.items_period[2].all_trades = tmp_tradingUnits.length;
      let total_volume = 0;
      let total_dur = 0;
      let long_dur = 0;
      let short_dur = 0;
      tmp_tradingUnits.map((tmp) => {
        // counting during time
        if (tmp.closeTime < "1000") {
          tmp.closeTime = new Date(
            Date.now() - new Date().getTimezoneOffset() * 60000
          ).toISOString();
        }
        let dur_time =
          new Date(tmp.closeTime).getTime() - new Date(tmp.openTime).getTime();
        total_dur += dur_time;
        //// counting volume and trades
        total_volume += tmp.volume;
        this.items_period[0].all_trades = (
          Number(this.items_period[0].all_trades) + Number(tmp.netProfit)
        ).toFixed(2);
        this.items_period[1].all_trades = (
          Number(this.items_period[1].all_trades) + Number(tmp.commission)
        ).toFixed(2);
        if (tmp.isBuy) {
          this.items_period[0].long_trades = (
            Number(this.items_period[0].long_trades) + Number(tmp.netProfit)
          ).toFixed(2);
          this.items_period[1].long_trades = (
            Number(this.items_period[1].long_trades) + Number(tmp.commission)
          ).toFixed(2);
          long_dur += dur_time;
          this.items_period[2].long_trades++;
          if (tmp.netProfit > 0) {
            this.items_period[3].long_trades++;
          } else this.items_period[5].long_trades++;
          if (tmp.netProfit > this.items_period[4].long_trades) {
            this.items_period[4].long_trades = Number(tmp.netProfit).toFixed(2);
          }
          if (tmp.netProfit < this.items_period[6].long_trades) {
            this.items_period[6].long_trades = Number(tmp.netProfit).toFixed(2);
          }
          this.items_period[9].long_trades += Number(tmp.volume);
        } else {
          this.items_period[0].short_trades = (
            Number(this.items_period[0].short_trades) + Number(tmp.netProfit)
          ).toFixed(2);
          this.items_period[1].short_trades = (
            Number(this.items_period[1].short_trades) + Number(tmp.commission)
          ).toFixed(2);
          short_dur += dur_time;
          this.items_period[2].short_trades++;
          if (tmp.netProfit > 0) {
            this.items_period[3].short_trades++;
          } else this.items_period[5].short_trades++;
          if (tmp.netProfit > this.items_period[4].short_trades) {
            this.items_period[4].short_trades = Number(tmp.netProfit).toFixed(
              2
            );
          }
          if (tmp.netProfit < this.items_period[6].short_trades) {
            this.items_period[6].short_trades = Number(tmp.netProfit).toFixed(
              2
            );
          }
          this.items_period[9].short_trades += Number(tmp.volume);
        }
        if (tmp.netProfit > 0) {
          this.items_period[3].all_trades++;
        } else {
          this.items_period[5].all_trades++;
        }
        if (tmp.netProfit > this.items_period[4].all_trades) {
          this.items_period[4].all_trades = Number(tmp.netProfit).toFixed(2);
        }
        if (tmp.netProfit < this.items_period[6].all_trades) {
          this.items_period[6].all_trades = Number(tmp.netProfit).toFixed(2);
        }
        this.items_period[9].all_trades += Number(tmp.volume);

        ////// Starting setting items_symbol
        this.items_symbol.map((sym, i) => {
          if (tmp.symbolName == sym.symbol) {
            sym.volume += tmp.volume;
            sym.trades++;
            if (tmp.netProfit > 0) sym.winning++;
            else sym.losing++;
            sym.netProfit = (
              Number(sym.netProfit) + Number(tmp.netProfit)
            ).toFixed(2);
            this.items_symbol[i] = sym;
          }
        });
      });
      this.items_period[7].all_trades =
        this.items_period[2].all_trades == 0
          ? 0
          : (
              this.items_period[0].all_trades / this.items_period[2].all_trades
            ).toFixed(2);
      this.items_period[7].long_trades =
        this.items_period[2].long_trades == 0
          ? 0
          : (
              this.items_period[0].long_trades /
              this.items_period[2].long_trades
            ).toFixed(2);
      this.items_period[7].short_trades =
        this.items_period[2].short_trades == 0
          ? 0
          : (
              this.items_period[0].short_trades /
              this.items_period[2].short_trades
            ).toFixed(2);
      total_dur =
        this.items_period[2].all_trades == 0
          ? 0
          : (total_dur / this.items_period[2].all_trades).toFixed();
      long_dur =
        this.items_period[2].long_trades == 0
          ? 0
          : (long_dur / this.items_period[2].long_trades).toFixed();
      short_dur =
        this.items_period[2].short_trades == 0
          ? 0
          : (short_dur / this.items_period[2].short_trades).toFixed();
      this.items_period[8].all_trades = this.calculating_during(total_dur);
      this.items_period[8].long_trades = this.calculating_during(long_dur);
      this.items_period[8].short_trades = this.calculating_during(short_dur);
      this.items_symbol.map((sym, index) => {
        this.series5[index].data[0] = 0;
        sym.volume_per =
          total_volume == 0
            ? 0
            : ((sym.volume / total_volume) * 100).toFixed(2) + "%";
        sym.winning_per =
          sym.winning == 0
            ? 0
            : ((sym.winning / sym.trades) * 100).toFixed() + "%";
        sym.losing_per =
          sym.losing == 0
            ? 0
            : ((sym.losing / sym.trades) * 100).toFixed() + "%";
        sym.avg_trade_size =
          sym.trades == 0
            ? 0
            : Number((sym.volume / sym.trades).toFixed(2));
        this.items_symbol[index] = sym;

        this.series5[index].data[0]=sym.trades;
      });
      this.updateChart5();
    },
    get_categories() {
      this.cate_week=[];
      this.cate_mon1=[];
      this.cate_chart1_mon3=[];
      this.cate_chart1_mon6=[];
      this.cate_chart1_year1=[];
      this.cate_chart1_all=[];
      this.cate_chart2_mon3=[];
      this.cate_chart2_mon6=[];
      this.cate_chart2_year1=[];
      this.cate_chart2_all=[];
      this.temp_chart1_week=[];
      this.temp_chart1_mon1=[];
      this.temp_chart1_mon3=[];
      this.temp_chart1_mon6=[];
      this.temp_chart1_year1=[];
      this.temp_chart1_all=[];
      this.temp_chart2_equity_week =[];
      this.temp_chart2_balance_week =[];
      this.temp_chart2_equity_mon1 =[];
      this.temp_chart2_balance_mon1 =[];
      this.temp_chart2_equity_mon3 =[];
      this.temp_chart2_balance_mon3 =[];
      this.temp_chart2_equity_mon6 =[];
      this.temp_chart2_balance_mon6 =[];
      this.temp_chart2_equity_year1 =[];
      this.temp_chart2_balance_year1 =[];
      this.temp_chart2_equity_all =[];
      this.temp_chart2_balance_all =[];

      let cur_date = new Date();
      let dates= cur_date.getDate();

      for(let i=30; i>=0; i--) {
        let tmp_date = new Date();
        tmp_date.setDate(Number(dates) - i);
        tmp_date=tmp_date.toISOString().split("T")[0];
        this.cate_mon1.push(tmp_date);
        if(i<=7) this.cate_week.push(tmp_date);
      }
      for (const key in this.chart1_data) {
        if (this.chart1_data[key] != null) {
          delete this.chart1_data[key];
        }
      }
      for (const key in this.chart2_data) {
        if (this.chart2_data[key] != null) {
          delete this.chart2_data[key];
        }
      }
      for (const key in this.chart3_data) {
        if (this.chart3_data[key] != null) {
          delete this.chart3_data[key];
        }
      }
      for (const key in this.chart4_data) {
        if (this.chart4_data[key] != null) {
          delete this.chart4_data[key];
        }
      }
      this.chart1_data = {};
      this.chart2_data = {};
      this.chart3_data = {};
      this.chart4_data = {};
    }
  },
  async mounted() {
    this.retrieveIDs();
    this.sel_chart1_period("all");
    this.sel_chart2_period("all");
    this.sel_chart4_period("month");
    this.sel_breakdown("trades");
    this.sel_history("history");
    this.change_period("cur_month");
  },
};
